async function run() {
  const client = await pool.connect();
  try {
    let inserted = 0;
    const batchSize = 25;
    
    for (let i = 0; i < occs.length; i += batchSize) {
      const batch = occs.slice(i, i + batchSize);
      const values = [];
      const params = [];
      let paramIdx = 1;
      
      for (const o of batch) {
        const ts = slug(o.title);
        const rs = Math.round(o.prob * 100);
        const level = rl(o.prob);
        values.push(`($${paramIdx},$${paramIdx+1},$${paramIdx+2},$${paramIdx+3},$${paramIdx+4},$${paramIdx+5},$${paramIdx+6},$${paramIdx+7},$${paramIdx+8},$${paramIdx+9},$${paramIdx+10},$${paramIdx+11},$${paramIdx+12},$${paramIdx+13})`);
        params.push(
          o.title, ts, o.cat, o.prob, level, rs, o.desc,
          JSON.stringify(o.tasks), JSON.stringify(o.risk), JSON.stringify(o.safe),
          o.ai, o.sector, o.emp, o.sal
        );
        paramIdx += 14;
      }
      
      const sql = `INSERT INTO occupations (title, title_slug, category, automation_probability, risk_level, risk_score, description, key_tasks, risk_factors, safe_factors, ai_impact_summary, related_sector, employment_count, median_salary) VALUES ${values.join(',')} ON CONFLICT (title_slug) DO NOTHING`;
      
      const result = await client.query(sql, params);
      inserted += result.rowCount;
      console.log(`Batch ${Math.floor(i/batchSize)+1}: inserted ${result.rowCount} rows`);
    }
    
    console.log(`\nTotal new rows inserted: ${inserted}`);
    const count = await client.query('SELECT count(*) as c FROM occupations');
    console.log(`Total occupations in database: ${count.rows[0].c}`);
    
    const cats = await client.query('SELECT category, count(*) as c FROM occupations GROUP BY category ORDER BY c DESC');
    console.log('\nBy category:');
    cats.rows.forEach(r => console.log(`  ${r.category}: ${r.c}`));
  } finally {
    client.release();
    await pool.end();
  }
}

run().catch(e => { console.error(e); process.exit(1); });
