function slugify(t) {
  return t.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
}

function riskLevel(score) {
  if (score <= 25) return 'low';
  if (score <= 50) return 'medium';
  if (score <= 75) return 'high';
  return 'very-high';
}

// Sector mapping for joining with layoffs table
function mapSector(onetCode, title, category) {
  const t = (title + ' ' + (category || '')).toLowerCase();
  if (/software|programmer|developer|web|computer|data|database|system admin|network admin|it /i.test(t)) return 'Technology';
  if (/financial|accountant|auditor|bank|credit|loan|tax|budget/i.test(t)) return 'Financial Services';
  if (/retail|cashier|sales|store|merchandise/i.test(t)) return 'Retail';
  if (/market|advertis|public relation|media buyer/i.test(t)) return 'Media & Advertising';
  if (/teacher|professor|education|instruc|tutor|librarian/i.test(t)) return 'Education';
  if (/nurse|doctor|physician|health|medical|dental|pharma|therap|surgeon|psycholog|psychiatr/i.test(t)) return 'Government/Healthcare';
  if (/manufactur|assembl|machin|weld|fabricat|production|plant/i.test(t)) return 'Manufacturing/Chemicals';
  if (/truck|driv|pilot|flight|ship|cargo|freight|logist|warehouse|postal|courier/i.test(t)) return 'Logistics';
  if (/telecom|telephone|cable/i.test(t)) return 'Telecom';
  if (/design|graphic|art director|ux|ui /i.test(t)) return 'Design Software';
  if (/game|gaming/i.test(t)) return 'Gaming/Software';
  if (/recruit|human resource|hr /i.test(t)) return 'Recruitment';
  if (/food|cook|chef|baker|bartend|waiter|waitress|restaurant/i.test(t)) return 'Food Delivery';
  if (/lawyer|attorney|judge|paralegal|legal/i.test(t)) return 'Law';
  if (/consult/i.test(t)) return 'Consulting';
  if (/secur|cyber|information security/i.test(t)) return 'Cybersecurity';
  if (/semiconductor|chip|electron/i.test(t)) return 'Semiconductors';
  if (/saas|cloud|enterprise/i.test(t)) return 'SaaS';
  if (/social media|content creator/i.test(t)) return 'Social Media';
  if (/ecommerce|e-commerce|online retail/i.test(t)) return 'E-commerce';
  if (/aviat|aerospace|airline/i.test(t)) return 'Aviation';
  return 'Technology'; // default
}

